const float PI = 3.141592653589793238462643383;

vec2 random2(vec3 st){
  vec2 S = vec2( dot(st,vec3(127.1,311.7,783.089)),
             dot(st,vec3(269.5,183.3,173.542)) );
  return fract(sin(S)*43758.5453123);
}

vec3 rand_dir( vec3 seed)
{
	vec2 uv = random2(seed);

	float theta = 2.0 * PI * uv[0];
	float phi = acos(2.0 * uv[1] - 1.0);

	return vec3(cos(theta) * sin(phi), sin(theta) * sin(phi), cos(phi));
}

vec3 smoooth(vec3 f){
    f[0] = 6.0 * pow(f[0], 5.0) - 15.0 * pow(f[0], 4.0) +  10.0 * pow(f[0], 3.0);
    f[1] = 6.0 * pow(f[1], 5.0) - 15.0 * pow(f[1], 4.0) +  10.0 * pow(f[1], 3.0);
    f[2] = 6.0 * pow(f[2], 5.0) - 15.0 * pow(f[2], 4.0) +  10.0 * pow(f[2], 3.0);
    return fract(f);
}

float perlin_noise(vec3 seed){
    vec3 p000 = rand_dir(floor(seed));
    vec3 p001 = rand_dir(floor(seed) + vec3(0,0,1));
    vec3 p010 = rand_dir(floor(seed) + vec3(0,1,0));
    vec3 p011 = rand_dir(floor(seed) + vec3(0,1,1));
    vec3 p100 = rand_dir(floor(seed) + vec3(1,0,0));
    vec3 p101 = rand_dir(floor(seed) + vec3(1,0,1));
    vec3 p110 = rand_dir(floor(seed) + vec3(1,1,0));
    vec3 p111 = rand_dir(floor(seed) + vec3(1,1,1));

    vec3 d000 = floor(seed) - seed;
    vec3 d001 = floor(seed) + vec3(0,0,1) - seed;
    vec3 d010 = floor(seed) + vec3(0,1,0) - seed;
    vec3 d011 = floor(seed) + vec3(0,1,1) - seed;
    vec3 d100 = floor(seed) + vec3(1,0,0) - seed;
    vec3 d101 = floor(seed) + vec3(1,0,1) - seed;
    vec3 d110 = floor(seed) + vec3(1,1,0) - seed;
    vec3 d111 = floor(seed) + vec3(1,1,1) - seed;

    vec3 smoothed = smoooth(fract(seed));

    float x_mix_1 = mix(dot(p000, d000), dot(p100, d100), smoothed[0]);
    float x_mix_2 = mix(dot(p001, d001), dot(p101, d101), smoothed[0]);
    float x_mix_3 = mix(dot(p010, d010), dot(p110, d110), smoothed[0]);
    float x_mix_4 = mix(dot(p011, d011), dot(p111, d111), smoothed[0]);

    float y_mix_1 = mix(x_mix_1, x_mix_3, smoothed[1]);
    float y_mix_2 = mix(x_mix_2, x_mix_4, smoothed[1]);

    return mix(y_mix_1, y_mix_2, smoothed[2]);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * vec2(2.0 * PI, PI);
    vec3 point = vec3(sin(uv[1]) * cos(uv[0]), sin(uv[1]) * sin(uv[0]), cos(uv[1])) * 15.0;

    vec3 point1 = vec3(point[0], point[1], point[2]);
    vec3 point2 = vec3(point[1], point[2], point[0]);
    vec3 point3 = vec3(point[2], point[0], point[1]);

    bool is_fract = false;

    float perlin1 = float(is_fract) * (fract(perlin_noise(point1)) + 1.0) * 0.5 + float(!is_fract) * (perlin_noise(point1) + 1.0) * 0.5;
    float perlin2 = float(is_fract) * (fract(perlin_noise(point2)) + 1.0) * 0.5 + float(!is_fract) * (perlin_noise(point2) + 1.0) * 0.5;
    float perlin3 = float(is_fract) * (fract(perlin_noise(point3)) + 1.0) * 0.5 + float(!is_fract) * (perlin_noise(point3) + 1.0) * 0.5;

    vec3 Perlin = vec3(perlin1, perlin2, perlin3);

    //vec3 col = vec3(fragCoord / iResolution.xy,0.0);
    fragColor = vec4(Perlin, 1.0);
}
