void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -factor to factor)
    float factor = 100.0+20.0*sin(iTime);
    vec2 uv2 = factor*((1.0*fragCoord/iResolution.xy)-vec2(0.5+sin(iTime)*sin(iTime*0.1+1.0)*0.5,0.5+cos(iTime)*sin(iTime*0.12+1.0)*0.5))/vec2(1.0,iResolution.x/iResolution.y);

    //distance
    float distance = (pow(pow(uv2.x,2.0)+pow(uv2.y,2.0),0.2));
    float gouga = -pow(iTime,1.1) + distance;

    //angle
    float angle = atan(uv2.x,uv2.y)+sin(iTime)*0.1+sin(distance*3.0)*0.5;

    // Time varying pixel color
    vec3 col = vec3(sin(gouga*3.1418)*0.5,-sin(gouga*3.1418)*0.5,sin(gouga*3.1418)*0.5+0.1);

    float tempMax1 = max(pow(sin(1.1*distance+5.0*iTime),1.0),0.0);
    float tempMax2 = max(sin(5.0*distance+3.0*iTime)*1.5-0.2,pow(sin(5.0*distance+3.0*iTime+2.5),1.0));
    float tempMax3 = max(0.0,min(1.0,max(tempMax1,tempMax2*3.5-3.0)));


    vec3 col2 = vec3(0.0,0.0,tempMax3*1.);

    // Output to screen
    fragColor = vec4(col2+(col*sin(angle*5.0)-sin(iTime)*0.25+0.2),1.0);
    //fragColor = vec4(col,1.0);
}
