float sawWaveOnInts(float x) {
    return 1.-abs(mod(2.*x-1.,2.)-1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord-iResolution.xy/2.;
    float scaleFactor = 0.1*mod(iTime, 30.);

    vec3 col = vec3(0.,0.,0.);

    col += vec3(0.,.7,0.) * sawWaveOnInts(length(uv)*scaleFactor);
    col += vec3(0.,0.,.7) * sawWaveOnInts(length(uv)*scaleFactor+.5);

    fragColor = vec4(col, 1.0);
}
