float boxSDF(vec2 pos, vec2 size) {
    vec2 d = abs(pos) - size * 0.5;

    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

float onion(float dist, float thickness) {
    return abs(dist) - thickness;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

    float d = boxSDF(uv, vec2(0.25, 0.2));
    d = max(d, -boxSDF(uv - vec2(0.125, 0.1 * sin(iTime)), vec2(0.25, 0.2)));
    d = onion(onion(d, 0.008), 0.004);
    vec3 col = (d > 0.0) ? vec3(0.0) : vec3(1.0);
    //col = vec3(q, 0.0);

    fragColor = vec4(col,1.0);
}
