#define t iTime
#define r iResolution.xy
#define outc fragColor

void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 xy = fragCoord.xy * .1;

    outc = vec4(
        vec3(0.32, 0.2, 1.) / smoothstep(0.2, 1., tan(
                pow(xy.x,2.) + pow(xy.y,2.) + iTime * 6.
            )) * 1.8,
        1.0
    );
}
