//-------------------------------------------
// KnitPattern_Extracted.glsl
// based on https://shadertoy.com/view/NdKcDc
//-------------------------------------------

float KnitPattern (vec2 p)
{
  float f = sin((p.x + p.y) * 111.);
  p *= vec2(45.,30.);
  p.y += abs( fract((p.x-1.)/2.) * 2. - 1.);
  float d = 1. - length(fract(p) - 0.5);
  return mix(f, d, 0.9);
}

void mainImage(out vec4 color, in vec2 coord)
{
  vec2 R = iResolution.xy;
  vec2 uv = (coord - 0.5*R.xy) / R.y;
  uv *= 0.1 + iMouse.y / R.y;  // zoom
  vec3 col = vec3(KnitPattern(uv));
  color = vec4(col, 1.0);
}
