vec2 random2(vec2 st){
  st = vec2( dot(st,vec2(127.1,311.7)),
             dot(st,vec2(269.5,183.3)) );
  return fract(sin(st)*43758.5453123);
}

vec2 smoooth(vec2 f){
    f[0] = 6.0 * pow(f[0], 5.0) - 15.0 * pow(f[0], 4.0) +  10.0 * pow(f[0], 3.0);
    f[1] = 6.0 * pow(f[1], 5.0) - 15.0 * pow(f[1], 4.0) +  10.0 * pow(f[1], 3.0);
    return fract(f);
}

float perlin_noise(vec2 seed){
    seed = seed * 0.01;

    vec2 p00 = random2(floor(seed));
    vec2 p01 = random2(floor(seed) + vec2(0,1));
    vec2 p10 = random2(floor(seed) + vec2(1,0));
    vec2 p11 = random2(floor(seed) + vec2(1,1));

    vec2 d00 = floor(seed) - seed;
    vec2 d01 = floor(seed) + vec2(0,1) - seed;
    vec2 d10 = floor(seed) + vec2(1,0) - seed;
    vec2 d11 = floor(seed) + vec2(1,1) - seed;

    vec2 smoothed = smoooth(fract(seed));

    return mix(mix(dot(p00, d00), dot(p10, d10), smoothed[0]), mix(dot(p01, d01), dot(p11, d11), smoothed[0]), smoothed[1]);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime / 2.;
    float perlin1 = pow(perlin_noise(1.2 * 0.5 * (sin(time) + 2.0) * fragCoord + 100.0 * vec2(sin(time * 2.0), cos(time * 3.0)) + vec2(time, 0.0) ), 1.0);
    float perlin2 = pow(perlin_noise(1.3 * 0.5 * (cos(time * 2.0) + 2.0) * vec2(fragCoord[1], fragCoord[0]) + 100.0 * vec2(sin(time * 4.0), cos(time * 5.0)) + vec2(0.0, time)), 1.0);
    float perlin = pow(fract(perlin1 + perlin2)/2.0, sin(2.0 * time) + 1.25);

    vec3 Perlin = vec3(perlin);

    //vec3 col = vec3(fragCoord / iResolution.xy,0.0);
    fragColor = vec4(Perlin, 1.0);
}
