float rand(vec2 n) {
  return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}
// 返回low ~ high的sin函数
float sint(float low, float high) {
  float t = sin(iTime*4.);
  float tall = high - low;
  return tall / 2. * t + tall / 2. + low;
}
// 爱心函数
float Heart(vec2 uv, vec2 p, float r, float blur) {
  uv *= vec2(.75, .95)*3.;
  uv -= p;
  float d = pow(uv.x, 2.) + pow(uv.y - pow(pow(uv.x, 2.), 1./3.), 2.) - 1.;
  return smoothstep(r, r-blur, d);
}

void mainImage(out vec4 fragColor,in vec2 fragCoord) {
  vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.xx;
  vec3 col = vec3(0.);
  vec3 baseCol = vec3(1.);
  uv *= 1.5;

  vec2 p = vec2(0., -.5);
  float edge = Heart(uv*.8*length(uv*.8)*1.8, vec2(0., -.47), .3, .8);
  vec3 col_edge = baseCol*edge;
  col_edge -= baseCol * Heart(uv*1.1, p, .3, 0.01);
  col_edge *= pow(rand(uv), 1./pow(length(uv), 4.*(2.2-sint(.7, 1.5))));

  float innerAnim = sint(length(uv)*1.5, 1.);
  float heart = Heart(uv * innerAnim, p, .3, 0.1);
  col = baseCol * heart * pow(rand(uv), 1./pow(length(uv), 2.5*(2.3-sint(.8, 1.5))));

  float border = Heart(uv * innerAnim, p, .3, .01);
  vec3 borderCol = baseCol * border * pow(rand(uv), 1./pow(length(uv), 2.5*(2.3-sint(.8, 1.5))));
  borderCol -= baseCol * Heart(uv * 1.3 * innerAnim, p, .3, .5);
  col += col_edge + borderCol*.2;
  col *= vec3(0.8392, 0.1882, 0.2314)*1.5;
  fragColor = vec4(col, 1.);
}
