float inv_circle(vec2 toCenter, float r)
{
    return smoothstep(r+0.01, r, length(toCenter));
}

vec3 a = vec3(0, 1.0, 1.0);
vec3 b = vec3(1.0, 0.0, 1.0);
vec3 c = vec3(0.3, 0.14, 0.38);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    vec2 center = vec2(0.5 * iResolution.x/iResolution.y, 0.5);
    vec2 toCenter = uv - center;
    float r = 0.4;

    vec3 col = mix(a,b,sqrt(uv.y));
    col *= inv_circle(toCenter, r);

    //put this into graphtoy or desmos
    float f = sin(exp((uv.y+0.4)*4.0))+0.7;
    col *= uv.y < 0.5 ? smoothstep(0.0, 0.3, f) : 1.0;

    //here i just wanted to set the background but it changes the colors
    //on the circle but i like how it looks so it's ok
    col += c; 

    fragColor = vec4(col,1.0);

}
