// ghostyhearts by technochroma
// see also my previous, "discotheque" https://www.shadertoy.com/view/mdlczs
// original "loonie" code by Crystalize, "gnarl" by jarble https://www.shadertoy.com/view/3syyDD
#define AA 3 

//loonie begin

vec2 loonie(vec2 z) {
    float r = dot(z,z);
    return r > 1. ? z : z * log(exp(2.0/r - 1.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )

{
    vec2 uv = 1.0 * (fragCoord.xy - 0.2*iResolution.xy) / -iResolution.y;

    float t = iTime;

    uv *= 1.1;
    uv = loonie(uv);
    uv -= vec2(t,t * 1.2 / 30.);
    uv *= 1.02;

//    float grid = mod(sin(uv.x)+cos(uv.y),2.);

     vec3 col;

// gnarls begin

    for(int c=0;c<12;c++){
//	    vec2 uv = (fragCoord*20.0-iResolution.xy)/iResolution.y;
        t = iTime;
        for(int i=3;i<7;i++)
        {
            uv /= 1.0;
            uv += sign(col.yx);
        	uv += float(i) + (sin(uv.x)*atan(uv.y)+sin(uv.y)*sin(iTime)+cos(iTime)*sign(uv.x)); 
        }
     col[c] = (cos(sin(uv.x+uv.y+iTime)));
	}

    fragColor = vec4(col,1.0);
}

// loonie end  

    // small bit of anti-aliasing (fixed 2)
    // drop this in voidmain after fragcolor

	vec2 of = vec2(0.3);

    #ifdef AA
	const float aa = float(AA);
    #else
    const float aa = 1.0;


	for(float i = 0.0; i < aa - 1.0; i++) {

		// super-sample around the center of the pixel.
		vec2 p = (-iResolution.xy + 2.0*(uv + of))/iResolution.y;
        col += render(p);
		of *= r(3.14159/8.0);

        	}

	col /= aa;

	col += 0.2*clamp(col, 0.0, 0.5);
	col = pow(col, vec3(1.0/2.2));

    #endif 

// anti-aliasing end
