#define maxIterations 4

#define PI 3.1415926

float circle(float radius, vec2 position, vec2 uv){

    float thickness = pow(radius, 2.0) * 0.025;
    //float val = 1.0 - smoothstep(thickness, thickness*2.0, abs(length(uv-position) - radius));
    float val = 1.0 - smoothstep(thickness, thickness*2.0, abs(dot(uv-position, uv-position) - pow(radius, 2.)));
    return val;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float speed = 0.2;
    float val = 0.0;
    float baseRadius = 0.5;

    float speedMultiplier = 1.618;
    float sizeMultiplier = 0.618;
    for (int iteration = 0; iteration < maxIterations; iteration++){
        float radius = baseRadius * pow(sizeMultiplier, float(iteration));
        // Loop through all circles in this iteration
        for (float c = 0.0; c < pow(4.0, float(iteration)); c++){ 
            vec2 position = vec2(0.0);
            // Move the circle
            for (float j=0.0; j < float(iteration); j++){
                float n = mod(floor(c/pow(4.0, float(iteration)-j-1.0)), 4.0);
                float angle = n*PI/2.0 + iTime*speed*pow(speedMultiplier, float(j));
                position += baseRadius*pow(sizeMultiplier, j) * vec2(cos(angle), sin(angle));
            }

            val += circle(radius, position, uv);
        }
    }
    val = 1.0 - val;

    // Output to screen
    fragColor = vec4(vec3(val), 1.0);
}
