void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord- 0.5*iResolution.xy)/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.y;

    vec3 col = vec3(1., 0.7, 0.);
    float y = uv.y *  3. *smoothstep(-0.3, -0.3, uv.x);

    float d = length(uv - vec2(clamp(uv.x, -.15, .5), y));
    d -= 1.-smoothstep(0.15, 0.15,length(uv - vec2(-.31, 0)));

    col = mix(col, vec3(step(uv.x+1., mouse.x+0.12)-0.05), smoothstep(0.15, 0.14, d));

    fragColor = vec4(col,1.0);
}
