void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y*2.5;
    vec3 col=vec3(0);

    // Change the values of a and b for
    // different lissajous figures 
    float a=3.0, b=5.0;

    float x=uv.x, y=uv.y, t=iTime/1.9, c=9000./x/y;

    for(float i=-.01; i<.01; i=i+.00011)
  { 
    float h=sin(c*i*a+t)*1.2, v=sin(c*i*b+t);
    if ((x+.02)>h && (x-.02)<h && (y+.1)>v && (y-.1)<v)
    col = vec3(.2,1,.2);
  }


    fragColor = vec4(col,1.);
}
