int gCount = 0;

vec2 jumpUsingIntensityAsAngle(vec2 uv, float p, float radius){
  vec3 color;
  if (gCount % 2 == 0) {
      color = texture(iChannel0,uv).xyz;
  }
  else {
      color = texture(iChannel1,uv).xyz;
  }
  float intensity = dot(color,vec3(1./3.f));
  float angle = radians((intensity + p)*360.f);
  return uv+vec2(cos(angle),sin(angle))*radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;


    const int num = 10;
    if(fragCoord.x > iMouse.x)
        for(int i=0;i<num;++i)
            uv = jumpUsingIntensityAsAngle(uv, float(-i) / float(num), 0.003f);



    fragColor = texture(iChannel0,uv);

}
