// source: https://iquilezles.org/articles/palettes/
vec3 palette( in float t )
{
    vec3 a = vec3(0.050, 0.666 , 0.785);
    vec3 b = vec3(0.561 , 0.825, 0.331);
    vec3 c = vec3(1.081, 1.448, 0.939);
    vec3 d = vec3(4.901, 3.497, 0.029);

    return a + b*cos( 6.28318*(c*t+d) );
}

//source: https://iquilezles.org/articles/distfunctions2d/
float sdET( in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float t = abs(iTime*0.5);
    t += sin(t);

    float d =0.0;

    for(float i = 0.0; i < 3.; i++) {
        uv = fract(uv)-0.5;

        float t = abs(iTime*1.);
        t += cos(t)*0.2;

        float d1 = sdET(uv, 0.01);
        d1 -= t + step(0.1,t) * 5.0 * sin(uv.x);
        d1 = smoothstep(0.0, 0.1, sin(d1* 8.)/16.);
        d += d1;
        //fragColor = vec4(vec3(d),1.0);
    }


    fragColor = vec4(palette(d), 1.0);
}
