Main {
    if ((int(U.x+U.y)+iFrame)%2==0) discard;

    Q = vec4(0);
    for (float x = -2.; x <= 2.; x++)
    for (float y = -2.; y <= 2.; y++) {

        vec2 V = B(U+vec2(x,y)/2.).xy;
        {
            vec2 s = abs(sin(V*.004));
            float z = min(s.x,s.y);

            Q += vec4(.25)/25.*(smoothstep(1.*fwidth(z),0.,z));
        }
        {
            vec2 s = abs(sin(V*.016));
            float z = min(s.x,s.y);

            Q += vec4(.25)/25.*(smoothstep(1.*fwidth(z),0.,z));
        }
        {
            vec2 s = abs(sin(V*.064));
            float z = min(s.x,s.y);

            Q += vec4(.25)/25.*(smoothstep(1.*fwidth(z),0.,z));
        }

        {
            vec2 s = abs(sin(V*.256));
            float z = min(s.x,s.y);

            Q += vec4(.25)/25.*(smoothstep(1.*fwidth(z),0.,z));
        }

        {
            float z = abs(V.y/R.y-.3);

            Q += vec4(1,1,0,1)/25.*(smoothstep(1.*fwidth(z),0.,z));
        }

        {
            float z = abs(V.x/R.x-.3);

            Q += vec4(0,1,1,1)/25.*(smoothstep(1.*fwidth(z),0.,z));
        }
    }
    Q = 1.-Q;

    //Q *= 1.-.005*length(vec2(fwidth(B(U).x),fwidth(B(U).x)));
    vec4 a = A(U);
    Q *= .9+.1*vec4(a.x-a.y,a.x,a.y,1);

}

vec4 T (vec2 U) {
    if (iFrame % 4 == 0) {
        U -= .5*v(A(U).xy);
        U -= .5*v(A(U).xy);
        U -= .5*v(A(U).xy);
        U -= .5*v(A(U).xy);
    }
    return A(U);
}
Main {

    Q = T(U);
    vec4 n = T(U+vec2(0,1));
    vec4 e = T(U+vec2(1,0));
    vec4 s = T(U-vec2(0,1));
    vec4 w = T(U-vec2(1,0));

    Q.xy -= .5*vec2(e.z-w.z,n.z-s.z);


    if (iMouse.z>0.&&length(U-iMouse.xy) < 10.) Q.y = .6;

    if (iFrame < 1) {
        vec2 v = normalize(U-.5*R);
        Q.xy = vec2(v.y,-v.x)*.1+v*sin(6.*atan(U.y-.5*R.y,U.x-.5*R.x));

        Q.xy *= 2.*exp(-10.*length(U-.5*R)/R.y);
    }

    if (U.x < 2.||R.x-U.x<2.||U.y < 2.||R.y-U.y<2.) Q.xy *= 0., Q.z *= .9;

}

vec4 T (vec2 U) {
    return A(U);
}
Main {

    Q = T(U);
    vec4 n = T(U+vec2(0,1));
    vec4 e = T(U+vec2(1,0));
    vec4 s = T(U-vec2(0,1));
    vec4 w = T(U-vec2(1,0));

    Q.z -= .5*(e.x-w.x+n.y-s.y);


}

#define R iResolution.xy
#define Main void mainImage(out vec4 Q, vec2 U)
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)

vec2 v (vec2 u) {
    return u*inversesqrt(.1+dot(u,u));
}

Main {
    if (iFrame % 4 == 0) {
        U -= .5*v(A(U).xy);
        U -= .5*v(A(U).xy);
        U -= .5*v(A(U).xy);
        U -= .5*v(A(U).xy);
    }

        Q = B(U);

        if (iFrame < 1) Q = vec4(U,0,0);

}
