void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float duration = 0.7;
    float maxAlpha = 0.5;
    float maxScale = 1.8;

    float progress = mod(iTime, duration) / duration;
    float currentAlpha = maxAlpha*(1.0-progress);
    float currentScale = 1.0 + (maxScale-1.0) * progress;

    vec2 uv = fragCoord/iResolution.xy;
    vec2 weakedUv = 0.5 + (uv - 0.5)/currentScale;

    vec4 weakMask = texture(iChannel0, weakedUv);
    vec4 origin = texture(iChannel0, uv);


    // Output to screen
    fragColor = origin* (1.0-currentAlpha) + weakMask*currentAlpha;
}
