vec3 lightPos;
const vec3 sunColour = vec3(1., 1., .6);

vec3 pathxyz(float z)
{
    vec3 p = vec3(cos((z *.1))*0.1, -sin((z *.1))*0.2,cos((z *.3))*0.1);
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;

    vec2 uv = (fragCoord - vec2(0.5 * iResolution.x, 0.5)) / iResolution.y;

    //uv += .0076/dot(r = fract(r)-.76,r);
    vec3 col2 = mix(vec3(uv.x, -uv.y, sin(iTime)), vec3(0.0, 0.0, 0.0), (.5 + length(uv)/sin(iTime+0.3) + 1.0/(0.9+iTime)));
    vec3 col3 = mix(vec3(-uv.x, uv.y, cos(iTime)), vec3(0.0, 0.0, 0.0), (.5 - length(uv)/sin(iTime+0.3) - 1.0/(0.9+iTime)));
    // Time varying pixel color
    vec3 col4 = (col2 + col3)/sin(1.0-iTime)+(uv.yxy*cos(col2+col3));
    vec3 col5 = sin(col3 /col2)*(uv.xyx*col4);
    vec3 col = cos(0.5+iTime)-col4*col5/cos(iTime+0.5);
    vec3 pos = pathxyz(iTime);
    lightPos = col/pos-vec3(pathxyz(iTime-sin(iTime*0.3)));  
    //
    fragColor.xyz = lightPos;
    fragColor *= texture(iChannel0, fragCoord/uv.xy) / length(uv);
    // Output to screen
    fragColor = vec4(col,1.0);
}
