// Another variant of "Simple fire effect" by guil. https://shadertoy.com/view/msyGRm

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = vec2(0.,iResolution.y+sin(iTime/25.0)*8.24),
          p = 5.*( u+u+vec2(800.,cos((iTime+5.)/10.0)*4000.) - R ) / R.y,
          r = p-p;

    for(float f=1.; f < 40.; f *= 1.05 )	
		r += sin( p*f +iTime*0.85) / f,
        p = p * mat2(8,6,-8,6)*.1 + r*.4;

    float l = length(r);
    O = vec4(l*0.29, l*l*0.024, l*l*l*0.0016, 0.);
}
