void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 px = 1.0/iResolution.xy;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 tex = texture(iChannel0,uv);
    vec4 tex2 = texture(iChannel0,uv)*(iTime+1.0);
    vec4 tex3 = texture(iChannel0,uv)*(iTime-1.0);

    //Getting the mask
    //getting the mask red channel
    float obt = 2.0;
    float mask_r = mix(dFdx(tex.r - tex2.r),dFdy(tex.r - tex3.r),0.5);

        //now trying to smooth the noise
    uv -= px;
    mask_r += mask_r*0.5;
    uv += px*1.0;
    mask_r -= mask_r*0.5;
    uv.x -= px.x*0.5;
    mask_r += mask_r*0.5;
    uv.y += px.y*0.5;
    mask_r -= mask_r*0.5;
    uv.x += px.x*0.5;
    mask_r += mask_r*0.5;
    uv.y -= px.y*0.5;
    mask_r -= mask_r*0.5;

    //getting the mask green channel
    float mask_g = mix(dFdx(tex.g - tex2.g),dFdy(tex.g - tex3.g),0.5);

        //now trying to smooth the noise
    uv -= px;
    mask_g += mask_g*0.5;
    uv += px*1.0;
    mask_g -= mask_g*0.5;
    uv.x -= px.x*0.5;
    mask_g += mask_g*0.5;
    uv.y += px.y*0.5;
    mask_g -= mask_g*0.5;
    uv.x += px.x*0.5;
    mask_g += mask_g*0.5;
    uv.y -= px.y*0.5;
    mask_g -= mask_g*0.5;

    //getting the mask blue channel
    float mask_b = mix(dFdx(tex.b - tex2.b),dFdy(tex.b - tex3.b),0.5);

        //now trying to smooth the noise
    uv -= px;
    mask_b += mask_b*0.5;
    uv += px*1.0;
    mask_b -= mask_b*0.5;
    uv.x -= px.x*0.5;
    mask_b += mask_b*0.5;
    uv.y += px.y*0.5;
    mask_b -= mask_b*0.5;
    uv.x += px.x*0.5;
    mask_b += mask_b*0.5;
    uv.y -= px.y*0.5;
    mask_b -= mask_b*0.5;



    //mixing it all in one
    float mask = mix(mask_r,mask_g,mask_b);


    //Making the blur

    if (mask != 0.0)
    {
        uv -= 0.5*px;
        tex2 = texture(iChannel1,uv);
        uv += px;
        tex2 += texture(iChannel1,uv);
        uv.y -= px.y;
        tex2 += texture(iChannel1,uv);
        uv.x -= px.x-.0001 *(iTime*.0001);
        uv.y += px.y+.0001 *(iTime*.0001);
        tex2 += texture(iChannel1,uv);
        tex2 /= 4.0;
        tex2 = clamp(tex2*1.0,0.1,1.1);
        tex = min(mix(tex,tex2,smoothstep(0.1,-0.1,mask)),clamp(tex*mask,1.0,1.0));
        //tex=mask_r;
     }



	fragColor = tex;
}
