vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

#define triwave_(p) abs(fract(.5+p/4.0)-.5)*2.
#define triwave1_(p) (abs(fract(p/8.0)-.5)-abs(fract(p/2.0)-.5)/2.)
float t1_(vec2 c, vec2 p){
    return triwave_((.5 - length( min(p=fract(p*sign(triwave1_(c*c.y))), 1.-p.yx) )));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 6.,c1=0.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.xy += iTime/t1/12.;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    vec3 col1 = col;
    for(int k = 0; k < 9; k++){

    uv.y -= t1_(floor(uv),floor(uv));


        uv = (uv+t2)/scale;

        //WOW!
        //uv = (fract(vec2(uv+vec2(.5,1.5))*2.)-.5)/2.;

        t2 = triangle_wave(uv-.5);
        uv =
            (t2-triangle_wave(uv.yx))
        ;

        c1 =
            //max(abs(uv.y-uv.x*sign(uv.x-uv.y))/2.,c1)
            max(abs(uv.y-uv.x)/2.,c1)
        ;
        c1 =
            max(abs(2.*c1-1.),c1/4.);

            //Spooky!
            //max(abs(2.*c1-2.),c1/4.)/2.;
        col.x =
            max(max(length(uv-t2-c1),abs(uv.y-uv.x))/3.,col.x);
        col =
            abs(col-(1.-(c1*col.x)));
        col1 =
            abs(col1*c1-col-1.).yzx;
    }
    fragColor = vec4(col1/2.,1.0);
}

float fmod(float a, float b){
    for(float i = 1.; i < mod(floor(a/b),5.); i++){
        a += mod(floor(a/2.),b)*i;
        a *= 1.+mod(floor(a)*2.,2.);
        i /= 1.+mod(floor(a)*2.,2.);
    }
    float a1 = a;
    return
        mod(floor(a),b)
    ;
}


vec2 mainSound(int samp, float time){
  float tempo = .25;
  time /= tempo;
  float s1 =
      8.;
  for(float i = 1.; i < mod(floor(time),5.); i++){
      time += mod(floor(time*2.),s1)*i;
      time /= 1.+fmod(floor(time)*2.,2.);
      tempo *= 1.+fmod(floor(time)*2.,2.);
  }
  float s2 = 8.,
  t=
      time,
  m1 =
      fmod(t,s2)
  ,
  m3 =
      fmod(t,1.+m1)+fmod(t/8.,2.);
  t /=
      (1.+fmod(t/s1,2.))*4./s1/s1
  ;
  float m2 =
      1. + fmod(t/s1,s1)
  ;
  float a=
      64.*(1.-sqrt(fract(t/s1)/1.25))*.2
      //pow(1.-log(1./16./2.+fract(t/s1)/4.),2.)
  ,
  nb = time*tempo*pow(2.,floor((m3+m2)/2.)/5.+6.5);
  return
      abs(.5-vec2(fract(nb*.998*2.),fract(nb)))*a

      //sitar
      //log(abs(a/4.-2.*abs(.5-vec2(fract(nb*.998*2.),fract(nb*2.)))*a))

      //log(abs(a/8.-abs(.5-vec2(fract(nb*.998*2.),fract(nb)))*a))
  ;
}
