// golfing 685 "Semi-circle Wave Animation" by Shane. https://shadertoy.com/view/cdycRt
// -10 by SnoopethDuckDuck -2 by Xor

void mainImage( out vec4 O,  vec2 u ){
    vec2  R = iResolution.xy,
          p = ( u+u - R ) / R.y;
    float N = 24., T = 6.283, H = .5, Z,
          a = atan( p.y+=H, p.x ) / T, 
          l = min( length(p*=N), N),
          i = floor( l ),
          A = sin(iTime/H + i*T/N*.75 ) *.243 + .25;

    p *= mat2(cos( T* ( p.x<Z && p.y<Z ? H : A ) - T*vec4(H,1,0,H)*H )); 

    p.y = fract( clamp(p.y, Z, N) ) - H; 

    O = sqrt( ( H + H*cos(i*T/N*.8 + vec4(2,4,6,0)) )
             *  smoothstep( 3.*N/R.y, Z, min( length(p), a < A ? N : abs(l-i-H) ) - .2 ));
}
