void mainImage(out vec4 O, vec2 I)
{
    for(
    //Initialize resolution for scaling
    vec3 r = iResolution,
    //Scrolling position
    p = iTime/r/r*8.;

    //Iterate 200 times
    r.z++<2e2;
    //Compute ray direction
    p += vec3(roundEven(I)*2.-r.xy,r)/r.y*
    //Tunnel distance field (inaccurate)
    (texture(iChannel0,tan(O.rgb=round(p))).r+.5-length(O.xy)*.3));

    //Use derivatives for edge lighting
    O = fwidth(O)/.1;
}
