void mainImage( out vec4 O, in vec2 U )
{
    vec2[] arvec = vec2[9](vec2(.2,.1),vec2(.4,.5),vec2(1.,.3),vec2(1.4,.3),vec2(1.2,.2),vec2(.8,.6),vec2(.2,0.7),vec2(.4,.3),vec2(1.5,.5));
    vec3[] arcol = vec3[9](c1,c2,c3,c4,c5,c6,c7,c8,c9);

    int l = arvec.length(); vec2 r = iResolution.xy; O *= 0.; vec2 u;

    for(float i = 1.,z = 1.0 - Random(u + i + iTime*0.001) * 0.35; i < float(l)+1.;
          //uv |     particle pos    |                          offset animation                             |
    u = (U/r.y - arvec[int(i-1.)].xy + vec2(-sin(iTime * 1.0 + i )  * .2, sin(iTime + i) * .2) * Rotate(iTime))
        //    scale | half scaled  animation                      |           rotate          | return pos
    * vec2(6.5 * z) * (1. - vec2(abs(sin(iTime  * 5. + i))) * .5) * Rotate(iTime * 2. +i-1.)  + vec2(.5),
      //         texture        |       color               | col power|       alpha                               |
    O += texture(iChannel0, u ) * vec4(arcol[int(i-1.)],1.) * 2.       * abs(1. - fract(iTime + i * i * .777) - .5)+i++*0.){}
}

void mainImage( out vec4 O, in vec2 U )
{
    vec2 r = iResolution.xy;
    vec2 n = U/r;
    vec4 tex = textureLod(iChannel0, n, 3.);
    vec3 crs = pow( vec3(1. - abs((n.y - .5) * (n.x - .5) )), vec3(255.6)) 
    * (1. - smoothstep(.1,.6,length(n-vec2(.5,.5))) /*add here*/ ) * tex.rgb;
    crs = pow(crs, vec3(.5) / crs);
    O.rgb = crs + crs + (1.-smoothstep(.01,.5,pow(length(n - vec2(.5)), .6)));
}

#define c1 vec3(1.,1.,1.)
#define c2 vec3(.2,.5,.5)
#define c3 vec3(.5,.3,.3)
#define c4 vec3(.7,.3,.8)
#define c5 vec3(.6,.2,.2)
#define c6 vec3(.4,.6,.6)
#define c7 vec3(.1,.7,.4)
#define c8 vec3(.2,.3,.9)
#define c9 vec3(.9,.2,.1)

mat2 Rotate(float angle)
{
    float s = sin(angle); float c = cos(angle);
    return mat2(c,-s,s,c);
}

float Random (vec2 uv) 
{
    return fract(sin(dot(uv.xy,vec2(1231.845679823,14351.130895732)))*43758.0456098);
}
