float hashS(float v, float seed) {
   v /= 1.66666;
   seed /= 1.6666;

   float p = cos((seed/2.2)-(v*0.001));
   p *= 1337.0;

   int i = int(p) % 1337;

   p = float(i) / 1337.0;

   float a = fract(cos(p+v) * atan(v, p) * dot(vec2(p, v), vec2(1.581, 0.981)));
   float b = fract(dot(vec2(v, (seed)), vec2(0.8271, 1.1928)));
   float c = fract((cos(b)*dot(vec2(a, b), vec2(1.8261-v, 0.8215+v))));

   vec3 v3 = textureLod(iChannel0, vec2(a+v, b+v)/max(seed, 256.0), 0.0).rgb;


   float res = mix(a, b, smoothstep(0.0, 1.0, c));


   res = mix(res, length(res), smoothstep(0.0, 1.0, v3.z-v3.x));
   return res;
}


float noise0(vec2 uv, float seed) {
    vec2 dir = vec2(1.0, 0.0);
    float mag = 0.03;

    vec2 id = floor(uv*1.6);
    vec2 lv = smoothstep(0.0, 1.0, fract(uv*1.6));
    float dx = hashS(hashS(id.x, seed*0.5)*uv.x, seed*0.9);
    float dy = hashS(hashS(id.y, seed*0.5)*uv.y, seed*0.9);
    float dz = fract(dot(uv, vec2(dx, dy)));

    return mix(fract(dx+dy+cos(dz*1.6)), dz, lv.y);
}

float noise1(vec2 uv, float seed) {
      vec2 id = floor(uv);
      vec2 lv = smoothstep(0.0, 1.0, fract(uv));
      float a = noise0(id, seed);
      float b = noise0(id+vec2(1.0, 0.0), seed);
      float ab = mix(a, b, lv.x);

      float c = noise0(id+vec2(0.0, 1.0), seed);
      float d = noise0(id+vec2(1.0, 1.0), seed);
      float cd = mix(c, d, lv.x);

      return mix(ab, cd, lv.y);
}

float noise(vec2 uv, float seed) {
    uv = (uv * 2.0 - 1.0);
    float a = noise1(uv, seed);
    float b = noise1(uv*1.6, seed+0.01);
    float c = noise1(uv*2.6, seed+0.02);
    float d = noise1(uv*3.14, seed+0.03);

    return (a + b + c + d) / 4.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float T = iTime*0.07;
    float off = noise((uv*0.5)+(vec2(3.181, 1.192) + vec2(sin(T), cos(T))), 128715.);
    float freq = 20.0 + ((0.1+((cos(T*12.6+off))))*12.6);

    vec3 col = vec3(noise((uv+vec2(cos(iTime*0.1+off), sin(iTime*0.1+off)))*freq, 98132.0+T));

    // Output to screen
    fragColor = vec4(col,1.0);
}
