#define MASK .92//(iMouse.x/iResolution.x)

int stipple[16]=int[16](
    1, 9, 3, 11,
    13, 5, 15, 7,
    4, 12, 2, 10,
    16, 8, 14, 6
);

void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 uv=fragCoord/iResolution.xy;
    vec4 col=texture(iChannel0, uv);

    int i=int(mod(fragCoord.x, 4.));
    int j=int(mod(fragCoord.y, 4.));

    vec3 thresh=vec3(
        stipple[(i+0)%4+((j+0)%4)*4],
        stipple[(i+1)%4+((j+1)%4)*4],
        stipple[(i+2)%4+((j+2)%4)*4]
    )/17.;
    fragColor=vec4(step(thresh*MASK, col.rgb), 1.);
}
