float WornLadder(float x,float l,float h,float d) {
  return min(mod(x,l)/d,h)+trunc(x/l)*h;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.y; uv *= 5.0;
    float bgfac = abs(trunc(mod(uv.x,2.0))-trunc(mod(uv.y,2.0)));

    float tt = iTime/2.0;
    float ll = 2.0+sin(tt*2.5)*1.5;
    float hh = 0.7+cos(tt)/2.0;
    float dd = 1.2+sin(tt*2.0)/2.0;

    float wl = float(WornLadder(uv.x,ll,hh,dd)>uv.y);

    vec3 col = vec3(mix(mix(.3,.5,bgfac),mix(.95,1.0,bgfac),wl));
    fragColor = vec4(col,0.0);
}
