void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float u_angle=(sin(iTime*0.5)-33.0)/10.0;
    float sin_factor = sin(u_angle);
    float cos_factor = cos(u_angle);
    vec2 coord = fragCoord/iResolution.xy;
    vec2 uv = vec2(coord.x - (0.6 * iResolution.x / iResolution.y), coord.y - 0.9) * mat2(cos_factor, sin_factor, -sin_factor, cos_factor);


    float fs=sin(iTime-uv.y*4.0)*3.0;
    float fs2=sin(iTime-(uv.y*4.0+1.5));

    float r= (fs+5.0)/100.0;



    fragColor = vec4(0,0,0,0);
    // Output to screen
    if(uv.x<(1.4/1.5+r))
    fragColor = vec4(0,0,0,0)*1.0-fs2*0.5;
    if(uv.x<(1.0/1.5+r))
    fragColor = vec4(1,1,0,0)*1.0-fs2*0.5;
    if(uv.x<1.0/3.0+r)
    fragColor = vec4(1,0,0,0)*1.0-fs2*0.5;
    if(uv.x<(0.0/1.5+r)&&uv.y<0.9)
    fragColor = vec4(0,0,0,0);
    if(uv.y>0.9||uv.y<0.1)
    fragColor = vec4(0,0,0,0);
}
