const float GRID_SIZE = 25.;
const float BORDER_SMOOTH = 0.02;

float grid(in vec2 fragCoord, float level)
{
    float c = abs(mod((fragCoord.x / GRID_SIZE), 2.) - 1.);
    c += abs(mod((fragCoord.y / GRID_SIZE), 2.) - 1.);
    c /= 2.;

    level = 0.5 + level / 2.0;

    c = smoothstep(level - BORDER_SMOOTH, level + BORDER_SMOOTH, c);

    return c;
}

float doubleGrid(in vec2 fragCoord, float level)
{
    return grid(fragCoord, level) + grid(fragCoord + GRID_SIZE, level);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float level = (cos(iTime) + 1.0) / 2.0;

    float c = doubleGrid(fragCoord + GRID_SIZE, level);

    fragColor = vec4(vec3(c), 1.0);
}
