void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float r= (sin(iTime-uv.y*1.5)+2.5)/6.0;

    if(r> uv.x *1.5){
        fragColor = vec4(0.95,0.7,0,1)*(r+0.6);
        }
    else if( r<= uv.x/1.5 ){
        fragColor = vec4(0,0.6,0.35,1)*(r+0.8);
        }
    else{
        fragColor = vec4(1,1,1,1)*(r+0.5);
    }

}
