vec2 clerp(vec2 v1, vec2 v2, float t) {
    return vec2( 
        v1.x * (1.-t) + v2.x * t,
        v1.y * (1.-t) + v2.y * t
    );
}

vec2 cmul(vec2 v1, vec2 v2) {
    return vec2(
        v1.x*v2.x - v1.y*v2.y,
        v1.x*v2.y + v1.y*v2.x
    );
}

vec2 cdiv(vec2 v1, vec2 v2) {
    float sc2 = 1. / (v2.x*v2.x + v2.y*v2.y);
    vec2 cinv2 = vec2(sc2 * v2.x, -1. * sc2 * v2.y);
    return cmul(v1, cinv2);
}

