vec2 rot_pt(vec2 p0, vec2 p, float ang_rad) {
    float s = sin(ang_rad);
    float c = cos(ang_rad);

    return vec2(
        (p.x - p0.x) * c - (p.y - p0.y) * s + p0.x,
        (p.x - p0.x) * s + (p.y - p0.y) * c + p0.y
    );
}

float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}

// line segment
float seg( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

// smoothstep
float SM( float x )
{
  return x*x*(3.0-2.0*x);
}
// lerp with time
vec2 lwt(in vec2 p0, in vec2 p1, in float tStart, in float tEnd) {
    vec2 diff = p1 - p0;
    float x = clamp((iTime - tStart) / (tEnd - tStart), 0., 1.);
    vec2 lerp_pt = p0 + (SM(x) * diff); 
    return lerp_pt;
}
// 2d vector lerp
vec2 lerp_2d(vec2 a, vec2 b, float lerp_v) {
    float t = clamp(lerp_v, 0., 1.);
    return (a + (b-a)*t);
}
// arclerp
// https://observablehq.com/@jrus/circle-arc-interpolation
// in complex plane:
//          cdiv(lerp( cmul((b-m), a), cmul((m-a),b), t)  
//  z(t) =
//
vec2 arc_lerp(vec2 a, vec2 m, vec2 b, float lerp_v) {
    return cdiv( 
        lerp_2d(cmul((b-m), a), cmul((m-a),b), lerp_v),
        lerp_2d(b-m, m-a, lerp_v)
    );
}

float lfwt(in float f0, in float f1, in float tStart, in float tEnd) {
    float diff = f1 - f0;
    float x = clamp((iTime - tStart) / (tEnd - tStart), 0., 1.);
    return f0 + (SM(x) * f1);
}




// arc: https://www.shadertoy.com/view/fdSyRh
float arcd(vec2 c, vec2 p1, vec2 p2, vec2 p) {

    vec2 v1 = p1 - c;
    vec2 v2 = p2 - c;
    v2 = normalize(v2)*length(v1); // Use angle only of p2
    vec2 v = p - c;

    vec2 w = vec2(dot(v, -vec2(-v1.y, v1.x)), dot(v, vec2(-v2.y, v2.x)));

    if(dot(v1, vec2(-v2.y, v2.x)) >= 0.0) { // Arc angle <= pi
        if(all(lessThan(vec2(0.0), w))) {
            return min(length(p1-p), length(p2-p));
        } else {
            return abs(length(v) - length(v1));
        }
    } else { // Arc angle > pi
        if(any(lessThan(vec2(0.0), w))) {
            return min(length(p1-p), length(p2-p));
        } else {
            return abs(length(v) - length(v1));
        }
    }
}

// 3-point arc lerp thingy
float iarc3(vec2 center, vec2 p1, vec2 p2, vec2 p3, float tStart, float tEnd, vec2 p) {
    float x = clamp((iTime - tStart) / (tEnd - tStart), 0., 1.);
    float lerp_val = (SM(x));
    vec2 start = p1;
    vec2 end = arc_lerp(p1, p2, p3, lerp_val);
    return arcd(center, start, end, p);
}

// reflection of P over line ay + bx + c = 0
// for our case only the 
vec2 rol(in vec2 p0, in float a, in float b, in float c) {
    float p = p0.x;
    float q = p0.y;
    float aa = a*a;
    float bb = b*b;
    float xCoord = (p * (aa - bb) - 2.*b*(a*q + c)) / (aa + bb);
    float yCoord = ( q * (bb - aa) - 2.*a*(b*p + c)) / (aa + bb);
    return vec2(xCoord, yCoord);
}

