float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

vec3 poison_apple=vec3(0.635,0.0,0.38);
vec3 teal_blue=vec3(0.0,0.329,0.533);
vec3 misty_green=vec3(0.811,0.894,0.819);
vec3 darkblue_green=vec3(0.0,0.435,0.482);
vec3 medium_gray=vec3(0.623,0.627,0.627);  

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 col;
    vec2 vec;
    float d;
    float AA=3.0/min(iResolution.x, iResolution.y);
    for(float i=0.0;i<60.0;i++)
    {
        vec=vec2(sin(mod(i,3.0))*(i*0.04)-0.1,sin(mod(i,3.0))*(i*0.04)-0.1);

        d=sdCircle(p+vec,i*0.07);

        col=(d>0.0&&mod(i,5.0)==0.) ? teal_blue      : col;
        col=(d>0.0&&mod(i,5.0)==1.) ? poison_apple   : col;
        col=(d>0.0&&mod(i,5.0)==2.) ? misty_green    : col;
        col=(d>0.0&&mod(i,5.0)==3.) ? darkblue_green : col;
        col=(d>0.0&&mod(i,5.0)==4.) ? medium_gray    : col;
        col = mix(col, vec3(0.0), smoothstep(AA,0.0,abs(d)) );

    }
    fragColor = vec4(col,1.0);
}
