vec4 loadImage(vec2 fragCoord)
{
    // Coordenadas da tela (x e y)
    vec2 uv = fragCoord/iResolution.xy * 1.0;

    vec4 image = texture(iChannel0, uv);

    return image;
}

vec4 psBW(vec4 color)
{
    float bw = (min(color.r, min(color.g, color.b)) + max(color.r, max(color.g, color.b))) * 0.5;
    return vec4(bw, bw, bw, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec4 image = loadImage(fragCoord);
    float mask = 0.0;
    float threshold = 0.16;
    float greenBias = 1.75;

    if (image.r - image.g > threshold * greenBias && image.r - image.b > threshold)
    {
        mask = 1.0;
    }

    vec4 finalImage;

    if (mask == 1.0)
    {
        // Colorir as áreas destacadas de azul
        finalImage = vec4(0.0, 0.0, 1.0, 1.0);
    }
    else
    {
        // Aplicar escala de cinza às áreas não destacadas
        finalImage = psBW(image);
    }

    fragColor = finalImage;


}
