vec3 U = vec3(1.,0.2,0.2);

float s = 0.02; //size

float f = 5.; //vertical frequency

float velocidad(float m){
    float o = 0.;

    float x = abs(m);

    if (0. <= x && x < 0.5)
    o = x*0.8;

    if (0.5 <= x && x <= 1.)
    o = (x*x + 0.25)*0.8;

    if (m < 0.)
    o = -o;

    return o;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;


    float k = 0.;
    float m = 0.;

    vec3 col = 0.*U;
    for (float i = 0.0; i < 20.0; i++){
    k = fract((iTime + 0.1*i)*0.5);
    m = velocidad(k);
    col += (1. + 0.2*sin(15.*(iTime + i)))*U/(length(uv/s - vec2(40.*cos(f*(iTime + 3.*sin(10.*i))),100.*m-45.)))*(1.-m);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
