void mainImage(out vec4 C, in vec2 U)
{
    vec2 R = iResolution.xy,
         m = iMouse.xy/R,
         u = (U-.5*R)/R.y/(1.-m.y); // coords with zoom
    vec3 c = vec3(0);
    float t = iTime/5.,
          a = atan(u.y, u.x), // screen arc
          r = length(u);      // screen radius
    u.x = tan(log(r)-a/2.*ceil(.1+m.x*10.)); // x to log spiral
    u.y = r; // y to radial
    u = max(abs(u), 1./abs(u)); // reciprocals

    // points move in the direction of Y at the speed of X
    vec2 o = vec2(0, t*round(u.x));
    c += min(4., 1./length((fract(u-o+.5)-.5) / fwidth(u)/1.5)) // point grid
       / max(1., abs(u.x)); // darken at 0 & inf

    C = vec4(c+c*c, 1);
}
