float random(vec2 uv){
    return fract(sin(uv.x*600.+uv.y*6400.)*8000.);
}

float smooth_Noise(vec2 uv){
    vec2 decimal_fraction= fract(uv);
    vec2 ID = floor(uv);


    decimal_fraction = sin(decimal_fraction*30.);

    float bl = random(ID+vec2(0,0));
    float br = random(ID+vec2(1.,0));
    float b = mix(bl,br,decimal_fraction.x);

    float tl = random(ID+vec2(0,1));
    float tr = random(ID+vec2(1,1));
    float t = mix(tl,tr,decimal_fraction.x);

    return  mix(b,t,decimal_fraction.y);
}


//Fractal Brownian Motion
float FBM(vec2 uv, int o,float a,float g,float f,float l){
    float v = 0.;
    float k = a;
    for(int i = 0;i<o;i++){
        v+=a*smooth_Noise(f*uv);
        a*=g;
        f*=l;
        k+=a;
    }
    v/=k;
    //from scratcha pixel
    //noiseMap[j * imageWidth + i] = (sin((i + noiseValue * 100) * 2 * M_PI / 200.f) + 1) / 2.f; 
    v = (sin((uv.x+v)*20.))/2.+0.5;
    return(v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv+iTime;

    int octaves = 1;
    float amplitude = 1.;
     float gain = 0.6;

    float frequency = 4.;
     float lacunarity = 1.8;


    float fbm_N = FBM(uv,octaves,amplitude,gain,frequency,lacunarity);
    vec3 col = vec3(fbm_N);
    fragColor = vec4(col,1.0);
}
