void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
}

#define RANDOMIZE_EDGES

const float oldMix      = 0.9999,    //feedback from last frame alpha
            noiseMix    = 0.01,   //rgb noise alpha
            noiseOffset = 0.5,    //value offset
            timeScale   = 0.5;

mat2 rotate(float r)
{
    return mat2( cos(r), -sin(r), sin(r), cos(r) );
}


vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 origin = vec2(0.5);
    float aspect = iResolution.x / iResolution.y;

    float T = iTime * timeScale;

    vec2 uvN = fragCoord / iResolution.xy;
    vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;

    vec2 uvW = uvN;                        //start of warp coordinates
    uvW -= 0.5;                            //apply origin offset
    uvW.x *= aspect;                       //apply aspect ratio stretch
    uvW *= rotate(0.005 * sin(T / 2.) );   //rotate image
    uvW /= 1. + 0.001 * cos(T);            //zoom image
    uvW.x /= aspect;                       //undo aspect ratio stretch
    uvW += 0.5;                            //undo origin offset
    uvW += 0.001 * vec2( cos(T), sin(T) ); //small offset

    vec3 rgbNoise = hash33(vec3(fragCoord.x, fragCoord.y, iTime));    
    vec3 oldColor = texture(iChannel0, uvW).rgb;

    #ifdef RANDOMIZE_EDGES
    if(
        fragCoord.x <= 1. || 
        fragCoord.y <= 1. ||
        fragCoord.x >= iResolution.x - 1. || 
        fragCoord.y >= iResolution.y - 1.)
            oldColor = rgbNoise;
    #endif


    //crazy color cycling
    float F = 0.2 + 0.025*cos(T);
    oldColor = vec3(oldColor.r - oldColor.g*F + oldColor.b*F, oldColor.g - oldColor.b*F + oldColor.r*F, oldColor.b - oldColor.r*F + oldColor.g*F);


    vec3 newColor = oldColor * oldMix + (rgbNoise - noiseOffset) * noiseMix;
    newColor = clamp(newColor, -1., 1.);



    fragColor = vec4(newColor, 1.);
}
