void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  	vec2 tc  = fragCoord/iResolution.xy;
  	vec2 p = -1.0 + 1.0 * tc;
 	float len = length(p);
	vec2 uv = tc + (p/len)*cos(len*12.0-iTime*4.0)*0.003;

    vec3 col =texture(iChannel0,uv).xyz;
 	fragColor = vec4(col,1.0);



}
