renderpass.code:
vec2 disort(vec2 p){
   p.x += sin(p.y*15.+iTime)/50.;
   // p.x += 3.*sin(p.y)/p.y;
    return p;
}

vec4 getFromColor(vec2 uv){
    vec2 rUv=uv;
    vec2 gUv=uv;
    vec2 bUv=uv;
    float offset=.05;
    rUv+=offset;
    bUv-=offset;
    vec4 rTex=texture(iChannel0,rUv);
    vec4 gTex=texture(iChannel0,gUv);
    vec4 bTex=texture(iChannel0,bUv);
    vec4 col=vec4(rTex.r,gTex.g,bTex.b,gTex.a);

    return col;
}

vec4 getToColor(vec2 uv){
    return texture(iChannel1,uv);
}

vec4 transition(vec2 uv){
    float progress=iMouse.x/iResolution.x;
    return mix(getFromColor(uv),getToColor(uv),1.-step(progress,uv.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=fragCoord/iResolution.xy;
    //uv=disort(uv);
    //vec3 tex=texture(iChannel1,uv).xyz;
    //fragColor=vec4(tex,1.);

    vec4 col=transition(uv);
    fragColor=col;
}
