vec3 color1 = vec3(0.5, 0.5, 0);
vec3 color2 = vec3(0., 0., 1.);
vec3 color3 = vec3(0.5, 0., 0.5);

vec2 rotate(vec2 uv, float th) {
    return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}

vec3 bgColor(vec2 uv) {
    uv -= 0.5;

    vec2 rotd = rotate(rotate(uv, iTime), 2. * (cos(iTime) - 0.5) * uv.y);

    vec3 col1 = mix(color1, color2, max(rotd.x, rotd.y)*3.);
    return mix(color3, col1, sin(iTime) * 3.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = bgColor(uv);

    fragColor = vec4(col, 1.0);
}
