precision highp float;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 p = 2.0*( fragCoord.xy / iResolution.xy ) -1.0;

	vec3 col = vec3(0);


	for (int i = 0; i < 100; i++) {
		float r = float(i)*0.01;		


		col += vec3(1.0)/(3.0+3000.0*(abs(length(sin(p.x*3237.0+p.y*100.888)*0.050+p.xy-vec2(0,sin(-iTime+float(i)*0.03)*float(i)*0.008))-r))); 

	}
	fragColor = vec4(col, 1.0);
}
