float rand(vec2 a) {
  float tmp = sin(a.x * 123.45 + a.y * 678.9);
  return fract(tmp  * 12.34);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
  uv = uv * 5.;
  uv += vec2(sin(iTime/2.), cos(iTime/2.)) * 5.;

  vec2 luv = fract(uv) - 0.5;
  vec2 id = floor(uv);

  // randomly flip a cell
  if (rand(id) < 0.5) {
    luv.y *= -1.;
  }

  float s = abs(luv.x + luv.y); // one stripe
  s = abs(s - 0.5); // shift & duplicate the stripe

  vec3 col = vec3(0);
  col += smoothstep(0.01, -0.01, s - 0.1);

  fragColor = vec4(col, 1.0);
}
