Main {
   vec4 H = .25*(A(U+vec2(0,1))+A(U+vec2(1,1))+A(U+vec2(1,0))+A(U+vec2(0,0)));
    Q = vec4(1);
    if (T) {
        Q = sin(-2.+1000.*length(A(U).xy)+vec4(1,2,3,4));
    } else {

        H *= 169.;
         Q *=(1.-exp(-length(H.xy)))*(1.-
             vec4(smoothstep(0.,2.*fwidth(cos(H.x)),abs(cos(H.x))))*
             vec4(smoothstep(0.,2.*fwidth(cos(H.y)),abs(cos(H.y)))));
    } 


    if (U.x<52.||U.y<52.||R.x-U.x<52.||R.y-U.y<52.)
        Q *= 0.;


    if (WA||WB||WC||WD) 
   {

        Q = .01*vec4(H.w,.25*abs(H.w),-H.w,1);

    }

}

#define R iResolution.xy
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define Main void mainImage(out vec4 Q, in vec2 U)
#define mu 1.
#define eps 1.
#define pi 3.141592659

#define Ro .01
#define Xi .1


float f (float x) {
    // Change the input signal here!
    return 10.*smoothstep(0.,1.,x)*sin(.02*x);
}


float roundbox ( vec2 p, vec2 b, float r) 
		{
		    vec2 h = 0.5*vec2(b.x,b.y);
		    vec2 q = p-clamp(p,-h+r,h-r);
		    if (length(q)>0.) return length(q) - r ;
            else return max(abs(p.x)-h.x,abs(p.y)-h.y);
		}


#define WA roundbox(U-vec2(.24,.5)*R,vec2(.01*R.y,.25*R.y),.01*R.x)<0.
#define WB roundbox(U-vec2(.37,.5)*R,vec2(.01*R.y,.25*R.y),.01*R.x)<0.
#define WC roundbox(U-vec2(.64,.5)*R,vec2(.01*R.y,.25*R.y),.01*R.x)<0.
#define WD roundbox(U-vec2(.77,.5)*R,vec2(.01*R.y,.25*R.y),.01*R.x)<0.
#define T max(roundbox(U-.5*R,vec2(.5,.6)*R,.05*R.y),-roundbox(U-.5*R,vec2(.3,.3)*R,.003*R.y))<0.

#define F 0
Main {
    Q = A(U);
    vec4 b = B(U);
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    if ((iFrame*2+F)%2==0) {
        // dB/dt = -curl E
        Q.x -=+.5/mu*(n.z-s.z)+Xi*b.x;
        Q.y -=-.5/mu*(e.z-w.z)+Xi*b.y;
    } else {
        // dE/dt = curl B - j
        Q.z -= .5/eps*(n.x-s.x-e.y+w.y) + Ro*Q.w;
    }
    Q.w += Q.z;
    if (WA) Q.w += f((float(iFrame)/60.))/R.y;
    else if (WB) Q.w +=-f((float(iFrame)/60.))/R.y;
    else if (WC) ;
    else if (WD) ;
    else Q.w = 0.;



    if (U.x<50.||U.y<50.||R.x-U.x<50.||R.y-U.y<50.)
        Q *= .98;
}

Main {

    Q = B(U);

    vec4 a = A(U);
    if (T)
    Q.xy = a.xy;


}

