void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragCoord.x -= iTime * 60.0;
    fragCoord.y += sin(iTime) * 32.;

    vec2 cell_size = vec2(32.0);
    float line_alpha = 0.3;
    float line_thickness = 6.0;

    vec2 cell = floor(fragCoord.xy / cell_size) * cell_size;

    float r = 0.0;
    if(fragCoord.x - cell.x < line_thickness || fragCoord.y - cell.y < line_thickness) {
        r = line_alpha;
    }

    fragColor = vec4(vec3(r), 1.0);
}
