renderpass.code:
#define S(v,s) smoothstep( 0., s 36./R.y, v ) // antialiasing, to black or white dep sign s

#define D(L,A,k) w = length( vec2( sqrt(L*L-d*d) - l*cos(A-a), d - l*abs(sin(A-a)) ) ),       \
                 s = mix( S( abs( w -.85 ) -.3, k ),                        /* ring end    */ \
                          abs(A-a)*l < 1. && abs(l-L) < .6 ? .5 + k .5 : s, /* ring or gap */ \
                          S( w - 1., ) )
//  general case, with pattern at angles wrap:  abs( mod(A-a +3.14, 6.283) - 3.14 ) < 1./l

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = 12.* ( u+u - R ) / R.y;
    float a = atan(U.y,U.x), d = 1.15, w,
          l = length(U),
          s = S( abs( mod( l < 11. ? l+1. : 0. , 2. ) - 1. ) -.45 , ); // main "target" shape

    D(10., -2.1 ,  );  // black bangles ends
    D( 8., -1.6 ,  );
    D( 6., -1.  ,  );
    D( 4., -.2  ,  );

    D( 9.,  1.  , -);  // white bangles ends
    D( 7.,  1.6 , -);
    D( 5.,  2.4 , -); 
    D( 3., -2.8 , -); 

    O = vec4( sqrt( s ));


}
