#define NUM_BARS 40.0
#define BAR_WIDTH (1.0 / NUM_BARS)
#define PI 3.14159
#define TWO_PI (2.0 * PI)
#define NUM_WAVES 3.0

float step2(float left, float right, float x)
{
    return step(left, x) * step(x, right);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    float current_bar = floor(uv.x / BAR_WIDTH);
    float bar_start = current_bar * BAR_WIDTH;
    float bar_end = bar_start + BAR_WIDTH;
    float offset = iTime * 2.0;
    float bar_height = (sin(offset + bar_start * TWO_PI * NUM_WAVES) + 1.0) / 2.0;
    float g = step2(bar_start, bar_end, uv.x) * step(uv.y, bar_height);

    fragColor = vec4(g*uv.y, g, bar_height*g/1.4, 1.0);
}
