mat2 rotate2D(float angle)
{
    return mat2(cos(angle),-sin(angle),sin(angle),cos(angle));
}

mat3 rotateY(float angle)
{
    return mat3(vec3(cos(angle),0,-sin(angle)), vec3(0,1,0), vec3(sin(angle),0,cos(angle)));
}

mat3 rotateX(float angle)
{
    return mat3(vec3(1,0,0), vec3(0,cos(angle),sin(angle)), vec3(0,-sin(angle),cos(angle)));
}

mat3 rotateZ(float angle)
{
    return mat3(vec3(cos(angle),-sin(angle),0), vec3(sin(angle),cos(angle),0), vec3(0,0,1));
}

vec3 project(vec3 p)
{
    return p / (1.0 + p.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;
    vec3 ray = vec3(p,2.0);
    mat3 rotation = rotateY(iTime * 0.5) * rotateX(iTime * 0.3);
    vec3 pos = vec3(0,0,5);
    vec3 dir = normalize(vec3(0,0,-1));
    vec3 side = normalize(vec3(1,0,0));
    vec3 up = normalize(vec3(0,1,0));
    mat3 cameraToWorld = mat3(side,up,dir);
    ray = cameraToWorld * ray;
    vec3 point = pos + ray * (pos.z / -ray.z);
    point = rotation * point;
    vec3 color = vec3(0.0);
    if (abs(point.x) < 1.0 && abs(point.y) < 1.0 && abs(point.z) < 1.0)
    {
        color = vec3(1.0);
    }
    fragColor = vec4(color,1.0);
}
