#define S(v) smoothstep( 36./R.y, 0., abs( mod( v < 11.5 ? v+.5 : 0. , 2. ) - 1. ) - .5  )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         A = 12.* abs( u+u - R ) / R.y;

    O = sqrt( max( S( length(A)/.97 + .6 )  * ( .9 - u/R ),
                   S( max(A.x,A.y) )        *     u/R
            )     ).yyyy;
}
