void mainImage( out vec4 O, vec2 U )
{
    U += U - (O.xy=iResolution.xy);
    O = fract(iTime - dot(acos(U/=O.y), 1e-3/U) + U.yyyy) - .5;
    O = exp(-8.*O*O);
}

